import React, { useEffect, useState } from "react";
import ProductCard from "../components/ProductCard";
import productService from "../services/productService";
import "./HomePage.css";

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    productService.getProducts().then((data) => {
      setProducts(data);
      setFilteredProducts(data);
    });
  }, []);

  const filterByCategory = (category) => {
    if (category === "all") {
      setFilteredProducts(products);
    } else {
      const filtered = products.filter((product) => product.category === category);
      setFilteredProducts(filtered);
    }
  };

  return (
    <div className="homepage">
      <h1>Our Products</h1>
      <div className="categories">
        <button onClick={() => filterByCategory("all")}>All</button>
        <button onClick={() => filterByCategory("mens")}>Mens</button>
        <button onClick={() => filterByCategory("womens")}>Womens</button>
        <button onClick={() => filterByCategory("kids")}>Kids</button>
        <button onClick={() => filterByCategory("electronics")}>Electronics</button>
      </div>
      <div className="product-grid">
        {filteredProducts.map((product) => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
