import React, { useEffect, useRef } from "react";
import "./HomePage.css";

const HomePage = () => {
  

  const cardContainerRef = useRef(null);

  useEffect(() => {
    const cardContainer = cardContainerRef.current;

    let scrollPosition = 0;
    const cardWidth = 200; // Adjust based on card width + gap
    const scrollSpeed = 2000; // Time in milliseconds

    const scrollCards = () => {
      if (cardContainer) {
        scrollPosition += cardWidth;

        // Reset to start if scrolled to the end
        if (scrollPosition >= cardContainer.scrollWidth) {
          scrollPosition = 0;
        }

        cardContainer.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    };

    // Set interval to auto-scroll
    const intervalId = setInterval(scrollCards, scrollSpeed);

    // Cleanup interval on unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="homepage">
      {/* Hero Section */}
      <section className="hero">
        {/* Removed Special Offer Text and Button */}
      </section>

      {/* Horizontal Scrollable Card Section */}
      <section className="horizontal-cards">
        <h2>Top Picks for You</h2>
        <div className="card-container" ref={cardContainerRef}>
          {/* Duplicate cards for smooth infinite scrolling */}
          {[...products, ...products].map((product, index) => (
            <div className="card" key={index}>
              <div
                className="card-image"
                style={{ backgroundImage: `url(${product.image})` }}
              ></div>
              <h3>{product.title}</h3>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
