import React, { useEffect, useRef } from "react";
import "./HomePage.css";

const HomePage = () => {
  const products = [
    { id: 1, title: "Men's T-Shirt", image: "/assets/product1.jpg" },
    { id: 2, title: "Women's Handbag", image: "/assets/product2.jpg" },
    { id: 3, title: "Kids' Toy Car", image: "/assets/product3.jpg" },
    { id: 4, title: "Smart Watch", image: "/assets/product4.jpg" },
    { id: 5, title: "Headphones", image: "/assets/product5.jpg" },
    { id: 6, title: "Laptop", image: "/assets/product6.jpg" },
  ];

  const scrollContainerRef = useRef(null);

  useEffect(() => {
    const scrollContainer = scrollContainerRef.current;

    let scrollSpeed = 1; // The speed of the scroll (pixels)
    let requestId;

    const scrollCards = () => {
      if (scrollContainer) {
        // Increment the scrollLeft property
        scrollContainer.scrollLeft += scrollSpeed;

        // If at the end, reset back to the start seamlessly
        if (
          scrollContainer.scrollLeft >=
          scrollContainer.scrollWidth / 2
        ) {
          scrollContainer.scrollLeft = 0;
        }
      }
      requestId = requestAnimationFrame(scrollCards);
    };

    // Start infinite scrolling
    requestId = requestAnimationFrame(scrollCards);

    // Cleanup the animation frame on unmount
    return () => cancelAnimationFrame(requestId);
  }, []);

  return (
    <div className="homepage">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Special Offer: 60% Off!</h1>
          <p>Get the best deals on all products.</p>
          <button className="shop-now-btn">Shop Now</button>
        </div>
      </section>

      {/* Horizontal Card Scroll Section */}
      <section className="horizontal-cards">
        <h2>Top Picks for You</h2>
        <div className="card-container" ref={scrollContainerRef}>
          {/* Duplicate cards for smooth infinite scrolling */}
          {[...products, ...products].map((product, index) => (
            <div className="card" key={index}>
              <div
                className="card-image"
                style={{
                  backgroundImage: `url(${product.image})`,
                }}
              ></div>
              <h3>{product.title}</h3>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
