import React, { useEffect, useRef } from "react";
import "./HomePage.css";

const HomePage = () => {
  const products = [
    { id: 1, title: "Men's T-Shirt", image: "/assets/product1.jpg" },
    { id: 2, title: "Women's Handbag", image: "/assets/product2.jpg" },
    { id: 3, title: "Kids' Toy Car", image: "/assets/product3.jpg" },
    { id: 4, title: "Smart Watch", image: "/assets/product4.jpg" },
    { id: 5, title: "Headphones", image: "/assets/product5.jpg" },
    { id: 6, title: "Laptop", image: "/assets/product6.jpg" },
  ];

  const cardContainerRef = useRef(null);

  useEffect(() => {
    const cardContainer = cardContainerRef.current;
    let scrollPosition = 0;
    const cardWidth = 220; // Adjust to your card width + gap
    const scrollSpeed = 2000; // Scroll interval in milliseconds

    const scrollCards = () => {
      if (cardContainer) {
        // Scroll position increment
        scrollPosition += cardWidth;

        // Reset scroll position when reaching the end
        if (scrollPosition >= cardContainer.scrollWidth / 2) {
          scrollPosition = 0;
        }

        cardContainer.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    };

    // Set interval for auto-scrolling
    const intervalId = setInterval(scrollCards, scrollSpeed);

    // Cleanup interval on unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="homepage">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Special Offer: 60% Off!</h1>
          <p>Get the best deals on all products.</p>
          <button className="shop-now-btn">Shop Now</button>
        </div>
      </section>

      {/* Horizontal Scrollable Card Section */}
      <section className="horizontal-cards">
        <h2>Top Picks for You</h2>
        <div className="card-container" ref={cardContainerRef}>
          {/* Duplicate cards for smooth infinite scrolling */}
          {[...products, ...products].map((product, index) => (
            <div className="card" key={index}>
              <div
                className="card-image"
                style={{
                  backgroundImage: `url(${product.image})`,
                }}
              ></div>
              <h3>{product.title}</h3>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
