import React, { useEffect, useRef } from "react";
import "./HomePage.css";

const HomePage = () => {
  const products = [
    { id: 1, title: "Men's T-Shirt", image: "/assets/product1.jpg", price: "$20" },
    { id: 2, title: "Women's Handbag", image: "/assets/product2.jpg", price: "$50" },
    { id: 3, title: "Kids' Toy Car", image: "/assets/product3.jpg", price: "$15" },
    { id: 4, title: "Smart Watch", image: "/assets/product4.jpg", price: "$100" },
    { id: 5, title: "Headphones", image: "/assets/product5.jpg", price: "$30" },
    { id: 6, title: "Laptop", image: "/assets/product6.jpg", price: "$500" }, // New Card
  ];

  const cardContainerRef = useRef(null);

  useEffect(() => {
    const cardContainer = cardContainerRef.current;

    let scrollPosition = 0;
    const cardWidth = 200; // Adjust this based on card width

    const scrollCards = () => {
      if (cardContainer) {
        scrollPosition += cardWidth; // Move right by one card width
        if (scrollPosition >= cardContainer.scrollWidth - cardContainer.clientWidth) {
          // If reached the end, reset to the start
          scrollPosition = 0;
        }
        cardContainer.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    };

    const intervalId = setInterval(scrollCards, 2000); // Scroll every 2 seconds

    // Cleanup interval on unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="homepage">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Special Offer: 60% Off!</h1>
          <p>Get the best deals on all products.</p>
          <button className="shop-now-btn">Shop Now</button>
        </div>
      </section>

      {/* Horizontal Scrollable Card Section */}
      <section className="horizontal-cards">
        <h2>Top Picks for You</h2>
        <div className="card-container" ref={cardContainerRef}>
          {products.map((product) => (
            <div className="card" key={product.id}>
              <div
                className="card-image"
                style={{ backgroundImage: `url(${product.image})` }}
              ></div>
              <h3>{product.title}</h3>
              <p>{product.price}</p>
              <button className="buy-btn">Buy Now</button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
