import React, { useEffect, useRef } from "react";
import "./HomePage.css";
import banner1 from "../assets/banner1.png";
import banner2 from "../assets/banner2.png";

const HomePage = () => {
  const products = [
    { id: 1, title: "Men's T-Shirt", image: "https://via.placeholder.com/200x150" },
    { id: 2, title: "Women's Handbag", image: "https://via.placeholder.com/200x150" },
    { id: 3, title: "Kids' Toy Car", image: "https://via.placeholder.com/200x150" },
    { id: 4, title: "Smart Watch", image: "https://via.placeholder.com/200x150" },
    { id: 5, title: "Headphones", image: "https://via.placeholder.com/200x150" },
    { id: 6, title: "Laptop", image: "https://via.placeholder.com/200x150" },
  ];

  const cardContainerRef = useRef(null);
  const bannerContainerRef = useRef(null);

  // Auto-scroll for cards
  useEffect(() => {
    const cardContainer = cardContainerRef.current;
    let scrollPosition = 0;
    const cardWidth = 200; // Width of card + gap
    const scrollSpeed = 2000;

    const scrollCards = () => {
      if (cardContainer) {
        scrollPosition += cardWidth;
        if (scrollPosition >= cardContainer.scrollWidth) {
          scrollPosition = 0; // Reset scroll position
        }
        cardContainer.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    };

    const cardInterval = setInterval(scrollCards, scrollSpeed);
    return () => clearInterval(cardInterval);
  }, []);

  // Auto-scroll for banners
  useEffect(() => {
    const bannerContainer = bannerContainerRef.current;
    let scrollPosition = 0;
    const scrollSpeed = 3000; // Adjust scroll time for banners

    const scrollBanners = () => {
      if (bannerContainer) {
        scrollPosition += bannerContainer.clientWidth;

        if (scrollPosition >= bannerContainer.scrollWidth) {
          scrollPosition = 0; // Reset back to the start
        }

        bannerContainer.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    };

    const bannerInterval = setInterval(scrollBanners, scrollSpeed);
    return () => clearInterval(bannerInterval);
  }, []);

  return (
    <div className="homepage">
      {/* Auto-scrolling Banners */}
      <section className="banner-section" ref={bannerContainerRef}>
        <div className="banner" style={{ backgroundImage: `url(${banner1})` }}></div>
        <div className="banner" style={{ backgroundImage: `url(${banner2})` }}></div>
        <div className="banner" style={{ backgroundImage: `url(${banner1})` }}></div>
        <div className="banner" style={{ backgroundImage: `url(${banner2})` }}></div>
      </section>

      {/* Auto-scrolling Horizontal Cards */}
      <section className="horizontal-cards">
        <h2>Top Picks for You</h2>
        <div className="card-container" ref={cardContainerRef}>
          {[...products, ...products].map((product, index) => (
            <div className="card" key={index}>
              <div
                className="card-image"
                style={{ backgroundImage: `url(${product.image})` }}
              ></div>
              <h3>{product.title}</h3>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
