import React, { useEffect, useRef} from "react";
import "./HomePage.css";
import banner1 from "../assets/banner1.png";
import banner2 from "../assets/banner2.png";

const HomePage = () => {
  const products = [
    { id: 1, title: "Men's T-Shirt", image: "https://via.placeholder.com/300" },
    { id: 2, title: "Women's Handbag", image: "https://via.placeholder.com/300" },
    { id: 3, title: "Kids' Toy Car", image: "https://via.placeholder.com/300" },
    { id: 4, title: "Smart Watch", image: "https://via.placeholder.com/300" },
    { id: 5, title: "Headphones", image: "https://via.placeholder.com/300" },
    { id: 6, title: "Laptop", image: "https://via.placeholder.com/300" },
  ];

  const trendingBrands = [
    { id: 1, name: "Nike", logo: "https://via.placeholder.com/150" },
    { id: 2, name: "Adidas", logo: "https://via.placeholder.com/150" },
    { id: 3, name: "Samsung", logo: "https://via.placeholder.com/150" },
    { id: 4, name: "Apple", logo: "https://via.placeholder.com/150" },
    { id: 5, name: "Sony", logo: "https://via.placeholder.com/150" },
    { id: 6, name: "Dell", logo: "https://via.placeholder.com/150" },
  ];

  const testimonials = [
    {
      id: 1,
      text: "This product changed my life! Absolutely love it.",
      author: "John Doe",
      position: "CEO, Tech Company",
    },
    {
      id: 2,
      text: "Great value for the price. Will definitely recommend it.",
      author: "Jane Smith",
      position: "Marketing Manager",
    },
    {
      id: 3,
      text: "Exceptional quality! Exceeded my expectations.",
      author: "Mark Johnson",
      position: "Product Designer",
    },
    {
      id: 4,
      text: "Fast shipping and great customer service. Highly satisfied.",
      author: "Emily Davis",
      position: "Entrepreneur",
    },
  ];

  const cardContainerRef = useRef(null);
  const bannerContainerRef = useRef(null);
  const trendingBrandsRef = useRef(null);
  const testimonialContainerRef = useRef(null);

  // Card Auto-Scroll Logic
  useEffect(() => {
    const cardContainer = cardContainerRef.current;
    let scrollPosition = 0;
    const cardWidth = 200;
    const scrollSpeed = 2000;

    const scrollCards = () => {
      if (cardContainer) {
        scrollPosition += cardWidth;
        if (scrollPosition >= cardContainer.scrollWidth) {
          scrollPosition = 0;
        }
        cardContainer.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    };

    const cardInterval = setInterval(scrollCards, scrollSpeed);
    return () => clearInterval(cardInterval);
  }, []);

  // Banner Auto-Scroll Logic
  useEffect(() => {
    const bannerContainer = bannerContainerRef.current;
    let scrollPosition = 0;
    const bannerWidth = bannerContainer.clientWidth;
    const scrollSpeed = 3000;

    const scrollBanners = () => {
      if (bannerContainer) {
        scrollPosition += bannerWidth;
        if (scrollPosition >= bannerContainer.scrollWidth) {
          scrollPosition = 0;
        }
        bannerContainer.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    };

    const bannerInterval = setInterval(scrollBanners, scrollSpeed);
    return () => clearInterval(bannerInterval);
  }, []);

  // Trending Brands Auto-Scroll Logic
  useEffect(() => {
    const trendingContainer = trendingBrandsRef.current;
    let scrollPosition = 0;
    const brandWidth = 150; // Width of the brand logo card
    const scrollSpeed = 2000;

    const scrollTrendingBrands = () => {
      if (trendingContainer) {
        scrollPosition += brandWidth;
        if (scrollPosition >= trendingContainer.scrollWidth) {
          scrollPosition = 0;
        }
        trendingContainer.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    };

    const trendingInterval = setInterval(scrollTrendingBrands, scrollSpeed);
    return () => clearInterval(trendingInterval);
  }, []);

  // Testimonial Auto-Scroll Logic
  useEffect(() => {
    const testimonialContainer = testimonialContainerRef.current;
    let scrollPosition = 0;
    const testimonialWidth = 400; // Width of the testimonial card
    const scrollSpeed = 3000;

    const scrollTestimonials = () => {
      if (testimonialContainer) {
        scrollPosition += testimonialWidth;
        if (scrollPosition >= testimonialContainer.scrollWidth) {
          scrollPosition = 0;
        }
        testimonialContainer.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    };

    const testimonialInterval = setInterval(scrollTestimonials, scrollSpeed);
    return () => clearInterval(testimonialInterval);
  }, []);

  // Toggle Menu

  return (
    <div className="homepage">
      
      {/* Auto-Scrolling Banner Section */}
      <section className="banner-section" ref={bannerContainerRef}>
        <div className="banner" style={{ backgroundImage: `url(${banner1})` }}></div>
        <div className="banner" style={{ backgroundImage: `url(${banner2})` }}></div>
        <div className="banner" style={{ backgroundImage: `url(${banner1})` }}></div>
        <div className="banner" style={{ backgroundImage: `url(${banner2})` }}></div>
      </section>

      {/* Search Bar */}
      <div className="search-bar-container">
        <input
          type="text"
          className="search-bar"
          placeholder="Search for products, categories, or brands..."
        />
      </div>

      {/* Horizontal Scrollable Product Card Section */}
      <section className="horizontal-cards">
        <h2>Top Picks for You</h2>
        <div className="card-container" ref={cardContainerRef}>
          {[...products, ...products].map((product, index) => (
            <div className="card" key={index}>
              <div
                className="card-image"
                style={{ backgroundImage: `url(${product.image})` }}
              ></div>
              <h3>{product.title}</h3>
            </div>
          ))}
        </div>
      </section>

      {/* Horizontal Scrollable Trending Brands Section */}
      <section className="horizontal-brands">
        <h2>Trending Brands</h2>
        <div className="brand-container" ref={trendingBrandsRef}>
          {[...trendingBrands, ...trendingBrands].map((brand, index) => (
            <div className="brand" key={index}>
              <div
                className="brand-logo"
                style={{ backgroundImage: `url(${brand.logo})` }}
              ></div>
              <h3>{brand.name}</h3>
            </div>
          ))}
        </div>
      </section>

      {/* Horizontal Scrollable Testimonial Section */}
      <section className="horizontal-testimonials">
        <h2>What Our Customers Say</h2>
        <div className="testimonial-container" ref={testimonialContainerRef}>
          {[...testimonials, ...testimonials].map((testimonial, index) => (
            <div className="testimonial" key={index}>
              <p>{`"${testimonial.text}"`}</p>
              <h4>{testimonial.author}</h4>
              <p>{testimonial.position}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
